import java.util.Queue;
import java.util.Set;
import java.util.LinkedList;
import java.util.HashSet;
public class Advent_06{
    public static void main(String[] args){
        System.out.println(getCharNum(4, getInputString()));
        System.out.println(getCharNum(14, getInputString()));
    }

    public static int getCharNum(int num, String s){
        Queue<Character> fifo = new LinkedList<Character>();
        char[] chars = s.toCharArray();
        for(int i = 0; i < num; i++) { fifo.add(chars[i]); }
        for(int i = num; i < chars.length; i++){
            if(!checkQueueForDups(fifo)) return i; 
            else{fifo.remove(); fifo.add(chars[i]);}
        }
        return 0;
    }
    public static boolean checkQueueForDups(Queue<Character> q){
        Set<Character> s = new HashSet<Character>();
        for(char c : q){
            if(s.contains(c)) return true;
            else s.add( c);
        }
        return false;
    }

    public static String getInputString(){
        return "";
    }
}
